const swiper=new Swiper(".swiper",{direction:"horizontal",loop:!0,slidesPerView:1,spaceBetween:10,breakpoints:{320:{slidesPerView:1,spaceBetween:20},768:{slidesPerView:2,spaceBetween:30},1200:{slidesPerView:3,spaceBetween:30}},pagination:{el:".swiper-pagination",type:"bullets",clickable:!0},autoplay:{delay:3e3,stopOnLastSlide:!0},a11y:{prevSlideMessage:"Previous slide",nextSlideMessage:"Next slide"},navigation:{nextEl:".swiper-button-next",prevEl:".swiper-button-prev"},observer:!0});function burgerMenu(){const e=document.querySelector(".burger"),s=document.querySelector(".menu"),i=document.querySelector("body");e.addEventListener("click",(()=>{s.classList.contains("active")?(s.classList.remove("active"),e.classList.remove("active-burger"),i.classList.remove("locked")):(s.classList.add("active"),e.classList.add("active-burger"),i.classList.add("locked"))})),window.addEventListener("resize",(()=>{window.innerWidth>991.98&&(s.classList.remove("active"),e.classList.remove("active-burger"),i.classList.remove("locked"))}))}burgerMenu();